import React, {Component } from 'react';
import './App.css';
import Navigation from './components/Nagivation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecogniiton from './components/FaceRecogniiton/FaceRecogniiton';
import Particles from 'react-particles-js';
import Clarifai, {COLOR_MODEL} from 'clarifai';


const app = new Clarifai.App({
  apiKey: '531dd1ccccf94ef5bab4cc112e8bccc1'
})

const particlesOptions = {
  particles: {
    number: {
      value: 30,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends Component {
  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
    }
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value})
  }

  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input})
    console.log('click')
    app.models.predict(
                COLOR_MODEL, 
                this.state.input).then(
    function(response) {
      // do something with response
    },
    function(err) {
      // there was an error
    }
  );
  }

  render(){
    return (
      <div className="App">
         <Particles className='particles'
              params={particlesOptions} 
          />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} 
                       onButtonSubmit={this.onButtonSubmit}/>
        <FaceRecogniiton imageUrl={this.setState.imageUrl} />
      </div>
    );
  }
}

export default App;
