import React, {Component, useCallback } from 'react';
import './App.css';
import Navigation from './components/Nagivation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecogniiton from './components/FaceRecogniiton/FaceRecogniiton';
import Particles from 'react-particles-js';
import Clarifai, { FACE_DETECT_MODEL} from 'clarifai';


const app = new Clarifai.App({
  apiKey: '531dd1ccccf94ef5bab4cc112e8bccc1'
})

const particlesOptions = {
  particles: {
    number: {
      value: 30,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

class App extends Component {
  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
    }
  }

  calculateFaceLocation = (data) => {
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputImage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.leftCol * width,
      topRow: clarifaiFace.topRow * height,
      rightCol: width - (clarifaiFace.rightCol * width),
      bottomRow: height - (clarifaiFace.bottomRow * height)
    }
  }

  displayFaceBox = (box) => {
    this.setState({ box: box});
  }

  onInputChange = (event) => {
    this.setState({input: event.target.value})
  }


  onButtonSubmit = () => {
    this.setState({imageUrl: this.state.input})
    app.models
              .predict(
                FACE_DETECT_MODEL, 
                this.state.input)
                .then(response => this.calculateFaceLocation(response))
                .catch(err => console.log(err));
  }

  render(){
    return (
      <div className="App">
         <Particles className='particles'
              params={particlesOptions} 
          />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm onInputChange={this.onInputChange} 
                       onButtonSubmit={this.onButtonSubmit}/>
        <FaceRecogniiton imageUrl={this.setState.imageUrl} />
      </div>
    );
  }
}

export default App;
